const alert={close:function(n){console.log(n);var t=n.closest(".alert");if(!t){console.warn("No alert found.");return}t.classList.add("fade-out");setTimeout(function(){t.remove()},400)}};(function(){let n=document.querySelectorAll(".alert a.close, .alert.close");for(var t of n)t.addEventListener("click",function(n){alert.close(this);n.preventDefault()})})();