const extendedTextareas={elements:{add:function(n){if(!n){console.error("Could not add element (no element).");return}if(n.nodeName!=="TEXTAREA"){console.error("Could not add element (not textarea).");return}n.setAttribute("rows","1");n.addEventListener("input",()=>{extendedTextareas.elements.resize(n)});n.addEventListener("resize",()=>{console.log("!")})},resize:function(n){if(n.style.height="",n.value.length!==0){let t=0;n.hasAttribute(extendedTextareas.settings.autoHeightOffsetAttribute)?t=parseInt(n.getAttribute(extendedTextareas.settings.autoHeightOffsetAttribute)):(t=n.offsetHeight-n.clientHeight+1,n.setAttribute(extendedTextareas.settings.autoHeightOffsetAttribute,t));n.style.height=n.scrollHeight+t+"px"}}},settings:{autoHeightAttribute:"data-auto-height",autoHeightOffsetAttribute:"data-auto-height-offset"}};(function(){let n=document.querySelectorAll(`textarea[${extendedTextareas.settings.autoHeightAttribute}]`);for(var t of n)extendedTextareas.elements.add(t)})();