function _horizontalTabs(n){this.timer=null;this.groups=n.querySelectorAll("details");this.moveGroups=()=>{if(this.groups&&!(this.groups.length<1)){const t=this.groups[0].parentElement.parentElement.getBoundingClientRect().left;for(var n=0;n<this.groups.length;n++){const i=this.groups[n].querySelector("nav");if(i){let r=this.groups[n].getBoundingClientRect();i.style.left=Math.round(r.left)+"px";this.groups[n].open?r.left<t-20?(this.groups[n].open=!1,i.classList.remove("open")):i.classList.add("open"):i.classList.remove("open")}}}};n.addEventListener("scroll",()=>{clearTimeout(this.timer),this.timer=setTimeout(this.moveGroups,100)});for(var t=0;t<this.groups.length;t++){let n=this.groups[t].querySelector("nav"),i=this.groups[t].querySelector("summary");n&&i&&setTimeout(()=>{n.style.width=Math.round(i.clientWidth)+"px"},100)}}(()=>{let t=document.querySelectorAll(".horizontal-tabs");for(var n=0;n<t.length;n++){let i=document.querySelector("details");i&&new _horizontalTabs(t[n])}})();